    // <editor-fold defaultstate="collapsed" desc="Metodos">  
    public void guardar() {
        String nombre = txtNombre.getText().toUpperCase();
        String apellido = txtApellido.getText().toUpperCase();
        String direccion = txtDireccion.getText().toUpperCase();
        String edad = cbmEdad.getSelectedItem().toString();
        String usuario = txtUsuario.getText().toLowerCase();
        String password = txtPassword.getText();
        boolean cond = true;
//        condicion que los txt no esten en nulos
        if (nombre != null && apellido != null && direccion != null && edad != "----------" && usuario != null && password != null) {
            for (int i = 0; i <objArchivo.LeerArchivoe().size(); i++) {
                if (usuario.equals(objArchivo.LeerArchivoe().get(i).getStrUsuario())) {
                    JOptionPane.showMessageDialog(rootPane, "Usuario ya existe intente nuevamente");
                    cond = false;
                }
            }
            if (cond == true) {
                clsEstudiantes objEstudiantes=new clsEstudiantes(nombre, apellido, direccion, edad, usuario, password);
                objArchivo.SaveEstudiantes(objEstudiantes);
                limpiar();
            }
        } else {
            JOptionPane.showMessageDialog(rootPane, "Por favor llene todos los campos");
        }
    }

    public void limpiar() {
        txtNombre.setText("");
        txtApellido.setText("");
        txtDireccion.setText("");
        cbmEdad.setSelectedIndex(0);
        txtUsuario.setText("");
        txtPassword.setText("");
    }

